CREATE TABLE IF NOT EXISTS plans (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), name TEXT NOT NULL, slug TEXT UNIQUE NOT NULL, description TEXT, price DECIMAL(10,2) NOT NULL DEFAULT 0, billing_cycle TEXT NOT NULL DEFAULT 'monthly' CHECK (billing_cycle IN ('monthly', 'yearly')), features JSONB DEFAULT '[]'::jsonb, modules_included TEXT[] DEFAULT '{}', max_storage_gb INTEGER DEFAULT 1, is_highlighted BOOLEAN DEFAULT false, status TEXT NOT NULL DEFAULT 'active' CHECK (status IN ('active', 'inactive')), created_at TIMESTAMPTZ DEFAULT NOW(), updated_at TIMESTAMPTZ DEFAULT NOW());;
