CREATE TABLE IF NOT EXISTS platform_modules (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), name TEXT NOT NULL, slug TEXT UNIQUE NOT NULL, description TEXT, icon TEXT, color TEXT DEFAULT '#6366f1', status TEXT NOT NULL DEFAULT 'active' CHECK (status IN ('active', 'inactive', 'beta')), sort_order INTEGER DEFAULT 0, created_at TIMESTAMPTZ DEFAULT NOW(), updated_at TIMESTAMPTZ DEFAULT NOW());;
